const API_URL="https://reqres.in/api/";export default class API{constructor(t){this.url=t}login({email:t,password:e}){return fetch(this.url+"login",{method:"POST",headers:{"content-type":"application/json"},body:JSON.stringify({email:t,password:e})}).then()}async fetchUsers(t){try{return await(await fetch(this.url+"users?page="+t.page,{method:"GET",headers:{"content-type":"application/json"}})).json()}catch(t){return console.log(t)}}async edit(t){console.log(t);try{return await(await fetch(this.url+"user/"+t.id,{method:"PUT",headers:{"content-type":"application/json"},body:JSON.stringify(t)})).json()}catch(t){return console.log(t)}}delete(t){return fetch(this.url+"users/"+t.id,{method:"DELETE"}).then(t=>t.ok).catch(t=>console.log(t))}create(t){return fetch(this.url+"users",{method:"POST",headers:{"content-type":"application/json"},body:JSON.stringify(t)}).then(t=>t.json()).then(t=>t).catch(t=>console.log(t))}}